package com.example.vuelos.dataset

import com.example.vuelos.model.Favoritos
import com.example.vuelos.model.Usuario
import com.example.vuelos.model.Vuelo

class Dataset {

    companion object {

        var listaUsuarios = arrayListOf<Usuario>()
        var listaVuelos = arrayListOf<Vuelo>()

        var listaFavoritos = arrayListOf<Favoritos>()

        fun agregarFavorito(favorito: Favoritos) {
            listaFavoritos.add(favorito)
        }



        fun agregarUsuario(usuario: Usuario) {
            listaUsuarios.add(usuario)
        }

        //funcion para comprobar qu no haya ningun usuario con el mismo correo

        fun comprobarCorreo(correo: String): Boolean {
            for (usuario in listaUsuarios) {
                if (usuario.correo == correo) {
                    return false
                }
            }
            return true
        }

        //funcion para comprobar que el correo y la contrase単a sean correctos

        fun comprobarLogin(correo: String, contrase単a: String): Boolean {
            for (usuario in listaUsuarios) {
                if (usuario.correo == correo && usuario.contrase単a == contrase単a) {
                    return true
                }

            }
            return false
        }

        fun agregarVuelo(vuelo: Vuelo) {
            listaVuelos.add(vuelo)
        }
        fun asignarImagenesCiudades(ciudad: String): String {
            when(ciudad) {
                "Madrid" -> return "data:image/jpeg;base64,"
                "Bilbao" -> return "data:image/jpeg;base64,"
                "Kingston" -> return "data:image/jpeg;base64,"
                "Londres" -> return "data:image/jpeg;base64,"
                "Paris" -> return "data:image/jpeg;base64,"
                "Roma" -> return "data:image/jpeg;base64,"
                else -> return "data:image/png;base64,"
            }

        }


        init {
            val nombre = "luis"
            val correo = "luis@gmail.com"
            val contrase単a = "123456"
            val perfil = "admin"
            val edad = 20
            val vuelos = ArrayList<Vuelo>()
            val favoritos = ArrayList<Favoritos>()

            val admin = Usuario(
                nombre,
                correo,
                contrase単a,
                perfil,
                edad,
                vuelos,
                favoritos
            )

            agregarUsuario(admin)
        }
    }
}
